Rules.InvariantScript:
Set(minAvaPhyLocation, 0);
Set(maxAvaPhyLocation, 0);
Reset(TextInput_MinAvaPhy_1);
Reset(TextInput_MaxAvaPhy_1);
Set(showFilterLocation, false);

If(
    IsNumeric(TextInput_MinAvaPhy_1.Text),
    Set(minAvaPhyLocation, Value(TextInput_MinAvaPhy_1.Text)),
    If(IsBlank(TextInput_MinAvaPhy_1.Text),
        Set(minAvaPhyLocation, 0),
        Notify("Input is not a valid number", NotificationType.Warning);
        Set(minAvaPhyLocation, 0);
        Reset(TextInput_MinAvaPhy_1));
)

If(
    IsNumeric(TextInput_MaxAvaPhy_1.Text),
        Set(maxAvaPhyLocation, Value(TextInput_MaxAvaPhy_1.Text)),
        If(IsBlank(TextInput_MaxAvaPhy_1.Text),
            Set(maxAvaPhyLocation, 0),
            Notify("Input is not a valid number", NotificationType.Warning);
            Set(maxAvaPhyLocation, 0);
            Reset(TextInput_MaxAvaPhy_1));
)
