Rules.InvariantScript:
ClearCollect(
    selectedWarehouseCollection,
    {}
);

If(
    selectedSiteAndWarehouse = ThisItem.Value,
    Set(
        selectedSiteAndWarehouse,
        ""
    );
    Set(
        selectedSite,
        ""
    ),
    Set(
        selectedSiteAndWarehouse,
        ThisItem.Value
    );
    Set(
        selectedSite,
        ThisItem.Value
    );
    ClearCollect(
        selectedWarehouseCollection,
        Filter(
            'Site and warehouse (mserp)',
            'Company Code' = Lower(selectedCompanyCode) && Site = ThisItem.Value
        )
    );

);
Set(
    selectedWarehouse,
    ""
);

Set(selectedWarehouse, ThisItem.Warehouse);

If(selectedSiteAndWarehouse = ThisItem.Site & ", " & ThisItem.Warehouse,
    Set(selectedSiteAndWarehouse, ""); Set(selectedWarehouse, ""); Set(selectedSite, ""),
    Set(selectedSiteAndWarehouse, ThisItem.Site & ", " & ThisItem.Warehouse); Set(selectedWarehouse, ThisItem.Warehouse); Set(selectedSite, ThisItem.Site)
);

Patch(
    'Inventory onhand mobile app statuses',
    First('Inventory onhand mobile app statuses'),
    {
        'Default site': selectedSite,
        'Default warehouse': selectedWarehouse
    }
);
Navigate(
    ProductSearch,
    ScreenTransition.None
);
Set(
    showSideMenu,
    true
);
