Rules.InvariantScript:
Set(
    selectedProductNumber,
    ThisItem.'Product number'
);
Set(
    selectedProductName,
    ThisItem.'Product name'
);
Set(
    selectedProductUnit,
    ThisItem.Unit
);
Set(
    userId,
    Text(User().EntraObjectId)
);
Patch(
    'Inventory onhand mobile request (mserp)',
    {
        'Company Code': selectedCompanyCode,
        'Product number': selectedProductNumber,
        'GUID string type': userId
    }
);
Refresh('Inventory onhand mobile entry (mserp)');
ClearCollect(
    localOnhandEntry_Site,
    Filter(
        'Inventory onhand mobile entry (mserp)',
        'GUID string type' = userId,
        AggregateType = 'InventInventoryOnHandMobileAggregateType (mserp)'.Site
    )
);
ClearCollect(
    allColors,
    AddColumns(
        Distinct(
            localOnhandEntry_Site,
            Color
        ),
        IsChosen,
        false
    )
);
ClearCollect(
    allSizes,
    AddColumns(
        Distinct(
            localOnhandEntry_Site,
            Size
        ),
        IsChosen,
        false
    )
);
ClearCollect(
    allStyles,
    AddColumns(
        Distinct(
            localOnhandEntry_Site,
            Style
        ),
        IsChosen,
        false
    )
);
ClearCollect(
    allConfigs,
    AddColumns(
        Distinct(
            localOnhandEntry_Site,
            Configuration
        ),
        IsChosen,
        false
    )
);

If(CountRows(allColors)>4, Set(colorsTooManyValues, true), Set(colorsTooManyValues, false));
If(CountRows(allSizes)>4, Set(sizesTooManyValues, true), Set(sizesTooManyValues, false));
If(CountRows(allStyles)>4, Set(stylesTooManyValues, true), Set(stylesTooManyValues, false));
If(CountRows(allConfigs)>4, Set(configsTooManyValues, true), Set(configsTooManyValues, false));

ClearCollect(
    InvDims, 
    {name: "Color", tooManyValues: colorsTooManyValues},
    {name: "Size", tooManyValues: sizesTooManyValues},
    {name: "Style", tooManyValues: stylesTooManyValues},
    {name: "Config", tooManyValues: configsTooManyValues}
);

Set(selectedInvDimColors, "");
Set(selectedInvDimSizes, "");
Set(selectedInvDimStyles, "");
Set(selectedInvDimConfigs, "");

Clear(chosenColors);
Clear(chosenSizes);
Clear(chosenStyles);
Clear(chosenConfigs);
Set(
    minAvaPhy,
    0
);
Set(
    maxAvaPhy,
    0
);
Reset(TextInput_MinAvaPhy);
Reset(TextInput_MaxAvaPhy);
Navigate(ProductDisplay)
